import React, { Component } from 'react';
import './App.css';
import { isMobile } from './utils.js';

class Project extends Component {
  render() {
    return (
      <div className={'row'}>
        <div
          className={(isMobile() ? 'two' : 'one') + ' wide top aligned column'}
          style={{ paddingRight: '0px', paddingTop: '1.6em' }}
        >
          <img src={this.props.img} alt={this.props.name} className={'ui rounded image'} />
        </div>
        <div className={(isMobile() ? 'fourteen' : 'fifteen') + ' wide column'}>
          <div className={'ui small header hel'}>
            <span
              style={{ fontSize: '80%', opacity: 0.5, fontWeight: 400 }}
            >
              {this.props.date}<br/>
            </span>
            {this.props.url ? 
              (<a href={this.props.url} target="_blank">
                {this.props.name} <span> <i className={'small disabled grey ' + this.props.icon + ' icon'}/></span>
              </a>) :
              (this.props.name)
            }
            <div className={'sub header hel'}>
              {this.props.desc}
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default Project;
