import React, { Component } from "react";
import "./App.css";
import { isMobile } from "./utils.js";
import Job from "./job.js";

class Jobs extends Component {
  render() {
    let jobs = [
      [
        "Consulting",
        "2018-Present",
        null,
        "Strategy & product design",
        "/logo_consulting.png",
        null
      ],
      [
        "Battery Gray",
        "2013-Present",
        "https://batterygray.com/",
        "We design & build software, mostly for ourselves",
        "/logo_bg.png",
        "2013-Present",
        null
      ],
      [
        "SV Angel",
        "2012-18",
        "https://svangel.com/",
        "Early stage investing; product & front-end programming",
        "/logo_svangel.jpg",
        null
      ],
      [
        "Forbes",
        "2016",
        "https://www.forbes.com/pictures/glil45jlj/abram-dawson-26/#6053565e1cb7",
        "Included in 30 Under 30 for venture capital",
        "/logo_forbes.png",
        "newspaper"
      ],
      [
        "Various Sartups",
        "2008-2012",
        null,
        "Mostly mobile consumer products in & after college",
        "logo_startups.png",
        null
      ]
    ];
    return (
      <div className={"ui " + (isMobile() ? "" : "padded") + " grid"}>
        {jobs.map(i => {
          return (
            <Job
              key={i[0]}
              name={i[0]}
              date={i[1]}
              url={i[2]}
              desc={i[3]}
              img={i[4]}
              icon={i[5]}
            />
          );
        })}
      </div>
    );
  }
}

export default Jobs;
