import React, { Component } from "react";
import "./App.css";
import { isMobile } from "./utils.js";

import Section from "./section.js";
import Projects from "./projects.js";
import People from "./people.js";
import Jobs from "./jobs.js";

class Sections extends Component {
  render() {
    let sections = [
      ["Projects", "Things I've created", <Projects />, "grey"],
      ["People", "Founders & companies I've worked with", <People />, "grey"],
      ["Professional", "Work experience & things of the sort", <Jobs />, "grey"]
    ];
    return (
      <div>
        {sections.map(i => {
          return (
            <Section
              name={i[0]}
              key={i[0]}
              desc={i[1]}
              title={i[2]}
              color={i[3]}
            />
          );
        })}
      </div>
    );
  }
}

class App extends Component {
  render() {
    return (
      <div className={"App"}>
        <div
          className={"ui text container"}
          style={{ paddingTop: isMobile() ? "10px" : "50px" }}
        >
        <div className="ui secondary segment">
          <p className={"hel"}>
            I'm currently designing &amp; building software in San Francisco. I
            recently converted a van into a tiny home, which I traveled &amp;
            lived in for the first half of 2019. Prior to building the van, I
            spent most of my 20s investing &amp; building software at SV Angel.
          </p>
          </div>
          <Sections />

        </div>
      </div>
    );
  }
}

export default App;
